        "policyDefinitionId": "/providers/Microsoft.Authorization/policyDefinitions/86efb160-8de7-451d-bc08-5d475b0aadae",
        "parameters": {
          "effect": {
            "value": "[[parameters('DataBoxCMKEffect')]"
          }
        },
