  "properties": {
    "policyType": "Custom",
    "displayName": "Deploy Microsoft Defender for Cloud configuration",
    "description": "Deploy Microsoft Defender for Cloud configuration",
    "metadata": {
      "version": "3.0.0",
      "category": "Security Center",
      "source": "https://github.com/Azure/Enterprise-Scale/",
