      },
      {
        "policyDefinitionReferenceId": "SynapseWorkspaceCMKEffect",
        "policyDefinitionId": "/providers/Microsoft.Authorization/policyDefinitions/f7d52b2d-e161-4dfa-a82b-55e564167385",
        "parameters": {
